## API Reference Item
- Name: GridProps.editable
- Type: Property
- Summary: The Grid editable settings.
- Syntax: 

## Examples
### Example 1
```
import * as React from 'react';
import { Grid, GridColumn, GridItemChangeEvent } from '@progress/kendo-react-grid';
const sampleData = [
    { id: 1, name: 'Alice', description: 'description 1' },
    { id: 2, name: 'Bob', description: 'description 2' },
    { id: 3, name: 'Charlie', description: 'description 3' }
];

export default function App() {
    const [data, setData] = React.useState<Array<{ id: number; name: string }>>(sampleData);

    const handleItemChange = (event: GridItemChangeEvent) => {
        const inEditID = event.dataItem.id;
        const field = event.field || '';
        const newData = data.map((item) => (item.id === inEditID ? { ...item, [field]: event.value } : item));
        setData(newData);
    };

    return (
        <Grid
            data={data}
            autoProcessData={true}
            editable={true}
            defaultEdit={{
                1: true
            }}
            dataItemKey="id"
            onItemChange={handleItemChange}
        >
            <GridColumn field="id" title="Id" />
            <GridColumn field="name" title="Name" />
            <GridColumn field="description" title="Description" />
        </Grid>
    );
}

```


